definition.script:
node('master') {

	def outputDir = "/var/www/vsc-builds/"+"${branch}"+"-all/"

    stage('ENV setup') {
        env.JAVA_HOME="${tool name: 'Java 8.121', type: 'jdk'}"
        env.PATH="${env.JAVA_HOME}/bin:${env.PATH}"
    }

    stage('Clean') {
        sh 'sudo rm * -rf'
    }

    stage('Git clone') {

        sh 'mkdir -p osc-core'
        dir ('osc-core') {
    		try {
    			git branch: '${branch}', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/osc-core.git'
    		} catch (err) {
                git branch: 'master', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/osc-core.git'
            }
        }

    	sh 'mkdir -p security-mgr-smc-plugin'
        dir ('security-mgr-smc-plugin') {
    		try {
    			git branch: '${branch}', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/security-mgr-smc-plugin.git'
    		} catch (err) {
                git branch: 'master', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/security-mgr-smc-plugin.git'
            }
        }

    	sh 'mkdir -p security-mgr-sample-plugin'
        dir ('security-mgr-sample-plugin') {
    		try {
    			git branch: '${branch}', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/security-mgr-sample-plugin.git'
    		} catch (err) {
                git branch: 'master', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/security-mgr-sample-plugin.git'
            }
        }

    	sh 'mkdir -p security-mgr-nsm-plugin'
        dir ('security-mgr-nsm-plugin') {
    		try {
    			git branch: '${branch}', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/security-mgr-nsm-plugin.git'
    		} catch (err) {
                git branch: 'master', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/security-mgr-nsm-plugin.git'
            }
        }

    	sh 'mkdir -p security-mgr-api'
        dir ('security-mgr-api') {
    		try {
    			git branch: '${branch}', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/security-mgr-api.git'
    		} catch (err) {
                git branch: 'master', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/security-mgr-api.git'
            }
        }

    	sh 'mkdir -p sdn-controller-api'
        dir ('sdn-controller-api') {
    		try {
    			git branch: '${branch}', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/sdn-controller-api.git'
    		} catch (err) {
                git branch: 'master', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/sdn-controller-api.git'
            }
        }

    	sh 'mkdir -p sdn-controller-nsc-plugin'
        dir ('sdn-controller-nsc-plugin') {
    		try {
    			git branch: '${branch}', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/sdn-controller-nsc-plugin.git'
    		} catch (err) {
                git branch: 'master', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/sdn-controller-nsc-plugin.git'
            }
        }
    }

    stage('Build OVF') {
        env.JAVA_HOME="${tool name: 'Java 8.121', type: 'jdk'}"
        env.PATH="${env.JAVA_HOME}/bin:${env.PATH}"
        dir ('osc-core') {
            def IMG_FORMAT = 'vmdk_only'
            switch (image_format) {
                 case "qcow2_only" :
                    echo "Build OSC qcow2 image"
                    IMG_FORMAT = 'qcow2-only'
                break
                 case "qcow2_and_vmdk" :
                    echo "Build OSC qcow2 and vmdk image"
                    IMG_FORMAT = 'qcow2-vmdk'
                break
                 case "raw_only" :
                    echo "Build OSC Raw image"
                    IMG_FORMAT = 'raw-only'
                break
            }
             sh 'ant ovf -Dimage-format='+IMG_FORMAT+ ' -Dall='+all
        }
    }

    stage('Publish') {
       dir ('osc-core') {
            buildVersion = sh(script: "ls | grep Build* | tr -d '\r' | tr -d '\n'", returnStdout: true)
            echo 'Found build: ' + buildVersion
            sh 'sudo rm ' + outputDir + buildVersion + ' -rf'
            sh 'sudo mv -v '+buildVersion+' ' + outputDir   
       }
    }

    //stage('Clean old builds') {
      //  sh 'cd '+outputDir+'; sudo rm `ls '+outputDir+' -t | awk \'NR>3\'` -rf'
//    }

    stage('Clean workspace') {
        sh 'sudo rm * -rf'
    }

}
