definition.script:
def buildVersion = ""
def outputDir = "/var/www/vsc-builds/osc-ovf-parametrized/"
def robotFrameworkTestsDir = "/home/jenkins/repos/robottests/"
def OSC_VM_IP=''

node('master') {

    stage('ENV setup master') {
        env.JAVA_HOME="${tool name: 'Java 8.121', type: 'jdk'}"
        env.PATH="${env.JAVA_HOME}/bin:${env.PATH}"
    }

    stage('Clean') {
        if(force_clean_workspace == "true"){
            sh 'sudo rm * -rf'
        }
    }

    stage('Git clone') {

        if(force_clean_workspace == "true"){
            sh 'mkdir -p osc-core'
        }
        pluginsBranch = "master"
        if (default_branch == "0.6") {
            pluginsBranch = "1.0.0"
        }

        dir ('osc-core') {
    		try {
    			git branch: '${branch}', credentialsId: 'oscservice-token', url: 'https://github.com/${fork}/osc-core.git'
    		} catch (err) {
                git branch: '${default_branch}', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/osc-core.git'
            }
        }		              

    	if(force_clean_workspace == "true"){
        	sh 'mkdir -p security-mgr-api'
    	}
        dir ('security-mgr-api') {
    		try {
    			git branch: '${branch}', credentialsId: 'oscservice-token', url: 'https://github.com/${fork}/security-mgr-api.git'
    		} catch (err) {
                git branch: pluginsBranch, credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/security-mgr-api.git'
            }
        }

    	if(force_clean_workspace == "true"){
    	    sh 'mkdir -p sdn-controller-api'
    	}
        dir ('sdn-controller-api') {
    		try {
    			git branch: '${branch}', credentialsId: 'oscservice-token', url: 'https://github.com/${fork}/sdn-controller-api.git'
    		} catch (err) {
                git branch: pluginsBranch, credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/sdn-controller-api.git'
            }
        }

		if(force_clean_workspace == "true"){
    	    sh 'mkdir -p security-mgr-sample-plugin'
    	}
        dir ('security-mgr-sample-plugin') {
    		try {
    			git branch: '${branch}', credentialsId: 'oscservice-token', url: 'https://github.com/${fork}/security-mgr-sample-plugin.git'
    		} catch (err) {
                git branch: pluginsBranch, credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/security-mgr-sample-plugin.git'
            }
        }

		if(force_clean_workspace == "true"){
    	    sh 'mkdir -p osc-pan-plugin'
    	}
        dir ('osc-pan-plugin') {
    		try {
    			git branch: '${branch}', credentialsId: 'oscservice-token', url: 'https://github.com/${fork}/osc-pan-plugin.git'
    		} catch (err) {
                git branch: pluginsBranch, credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/osc-pan-plugin.git'
            }
        }

    	if(force_clean_workspace == "true"){
            sh 'mkdir -p sdn-controller-nsc-plugin'
    	}
        dir ('sdn-controller-nsc-plugin') {
    		try {
    			git branch: '${branch}', credentialsId: 'oscservice-token', url: 'https://github.com/${fork}/sdn-controller-nsc-plugin.git'
    		} catch (err) {
                git branch: pluginsBranch, credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/sdn-controller-nsc-plugin.git'
            }
        }

		if(force_clean_workspace == "true") {
			sh 'mkdir -p osc-nuage-plugin'
		}
		dir ('osc-nuage-plugin') {
			try {
				git branch: '${branch}', credentialsId: 'oscservice-token', url: 'https://github.com/${fork}/osc-nuage-plugin.git'
			} catch (err) {
				git branch: '${default_branch}', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/osc-nuage-plugin.git'
			}
		}

		if(force_clean_workspace == "true") {
			sh 'mkdir -p sdn-controller-nsfc-plugin'
		}		
        dir ('sdn-controller-nsfc-plugin') {
    		try {
    			git branch: '${branch}', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/sdn-controller-nsfc-plugin.git'
    		} catch (err) {
                git branch: 'master', credentialsId: 'oscservice-token', url: 'https://github.com/opensecuritycontroller/sdn-controller-nsfc-plugin.git'
            }
        }
    }

    stage('Run junit tests') {
        if(enable_junit_tests == "true") {
            try{
                dir ('osc-core') {
                    sh 'mvn clean install -Dcheckstyle.skip=true -P all'
                }
            } finally {
                step([$class: "JUnitResultArchiver", testResults: '**/target/surefire-reports/TEST-*.xml, **/target/failsafe-reports/TEST-*.xml'])
                step([$class: "CheckStylePublisher",
                      canComputeNew: false,
                      defaultEncoding: "",
                      healthy: "",
                      pattern: "**/target/checkstyle-result.xml",
                      unHealthy: ""])
            }
        } else {
            echo "Skipped"
        }
    }

    stage('Build OVF') {
        if(skip_ovf_build == "true"){
            echo 'Skipped'
        } else {
            dir ('osc-core') {
                sh 'ant ovf -Dall='+all
            }
        }
    }

    stage('Publish') {
        dir ('osc-core') {
			if(skip_ovf_build == "true"){
			    if(deploy == "nodeploy") {
			        sh 'cd '+outputDir+'upgradebundle/; sudo rm `ls '+outputDir+'upgradebundle/ -t | awk \'NR>4\'` -rf'
				    sh 'sudo mv -v target/*.zip ' + outputDir + 'upgradebundle/serverUpgradeBundle_'+ env.BUILD_NUMBER+'.zip'
				    echo "http://10.3.240.52/osc-ovf-parametrized/upgradebundle/serverUpgradeBundle_"+ env.BUILD_NUMBER+".zip"
			    } else {
			        echo 'Skipped'
			    }
			} else {
			    buildVersion = sh(script: "ls | grep Build* | tr -d '\r' | tr -d '\n'", returnStdout: true)
    			echo 'Found build: ' + buildVersion
    			sh 'sudo rm ' + outputDir + buildVersion + ' -rf'
				sh 'sudo mv -v '+buildVersion+' ' + outputDir
				echo "http://10.3.240.52/osc-ovf-parametrized/"+buildVersion+"/"
			}
	   }
    }

    stage('Clean old builds') {
        if(skip_ovf_build == "true"){
            echo 'Skipped'
        } else {
            sh 'cd '+outputDir+'; sudo rm `ls '+outputDir+' -t | awk \'/Build/\' | awk \'NR>4\'` -rf'
        }
    }

	stage('Fetch OVF from Jenkins') {
	    if(skip_ovf_build == "true" || deploy == "nodeploy") {
            echo "Skipped"
        } else {
    		sh 'mkdir -p ovf'
    		dir('ovf'){
    			sh 'wget http://10.3.240.52/osc-ovf-parametrized/'+buildVersion+'/OSC_disk-0.vmdk --no-proxy'
    			sh 'wget http://10.3.240.52/osc-ovf-parametrized/'+buildVersion+'/OSC-' + buildVersion[5..-1]+ '.ovf --no-proxy'
    		}
        }
	}

	stage('Deploy OVF') {
        if(deploy == "nodeploy") {
            echo "Skipped deployment"
        } else {
            echo "Deploy on ${deploy}"

            // vSphere/vCenter settings
            def VSPHERE_SDK_URL = 'https://XX.XX.XXX.XXX/sdk'
            def VSPHERE_USER = 'root'
            def VSPHERE_PASSWORD = 'vmware'

            // VM deployment settings
            def ESXI_HOST_IP = 'XX.XX.XXX.XXX'
            def DATASTORE_NAME = 'Data'
            def NETWORK_NAME = 'VM Network'

			def OSC_VM_NAME=''
			def OSC_VM_NETMASK='255.255.254.0'
			def OSC_VM_GATEWAY='XX.XX.XXX.X'
			def OSC_VM_DNS='10.102.60.20 10.102.60.30'

            switch (deploy) {
                 case "common": 
                    echo "Using universal machine settings"
                    // OSC VM settings
                    OSC_VM_NAME='OSC_common'
                    OSC_VM_IP='XX.XX.XXX.XXX'
                 break
            }
            if(skip_ovf_build == "false"){
                def OVF_LOCAL_PATH = env.WORKSPACE + '/ovf/OSC-' + buildVersion[5..-1]+ '.ovf'

                sh 'java -jar /home/jenkins/repos/osc-tools/deploy-ovf/target/deploy-ovf.jar '+VSPHERE_SDK_URL+' '+VSPHERE_USER+' '+VSPHERE_PASSWORD+' '+ESXI_HOST_IP+' '+ OVF_LOCAL_PATH +' '+OSC_VM_NAME+' '+DATASTORE_NAME+' "'+NETWORK_NAME+'" '+OSC_VM_IP+' '+OSC_VM_NETMASK+' '+OSC_VM_GATEWAY+' "'+OSC_VM_DNS+'"' 
				sh 'rm ovf -rf'
            }

        }
    }   

	stage('Health check') {
		if(deploy == "nodeploy") {
			echo "Skipped health check"
		} else {
			// OSC settings
			def OSC_API_PORT=8090

			// FailSafe setings
			INTERVAL=30
			MAX_RETRY_NUMBER=10

			sh 'java -jar /home/jenkins/osc-tools/check-machine.jar '+OSC_VM_IP+' '+OSC_API_PORT+' '+INTERVAL+' '+MAX_RETRY_NUMBER
		}
	}

	stage('Run Serenity tests') {
		if(deploy == "nodeploy" || enable_web_tests == "false" || OSC_VM_IP == ''){
			echo 'Skipped run serenity tests'
		} else {
			env.DISPLAY=':0'
			sh 'sed -ie \'s|webdriver.base.url=.*|webdriver.base.url=https://'+OSC_VM_IP+'/|g\' /home/jenkins/repos/osc-tools/automated-tests/serenity.properties'
			sh 'xhost +localhost'
			try {
			    sh 'mvn clean install -f /home/jenkins/repos/osc-tools/automated-tests/'
			} finally {
			    sh 'rm serenity/ -rf'
			    sh 'mv /home/jenkins/repos/osc-tools/automated-tests/target/site/serenity $WORKSPACE/'
				stash name: "serenity-output", includes: "serenity/**/*"

				node('master') {
					// Run the unstash from within that directory!
					sh 'mkdir -p serenity-output'
					dir("serenity-output") {
						unstash "serenity-output"
						if(buildVersion == "") {
						    sh 'sudo rm '+outputDir+'last_web_tests -rf'
						    sh 'sudo mv -v serenity/ ' + outputDir +"last_web_tests"
						    echo "http://10.3.240.52/osc-ovf-parametrized/last_web_tests/"
						} else {
						    sh 'sudo mv -v serenity/ ' + outputDir + buildVersion + "/"
						    echo "http://10.3.240.52/osc-ovf-parametrized/"+buildVersion+"/serenity/"
						}
					}
				}
			}
		}
	}

	stage('Run robot framework tests') {
	    if(deploy == "nodeploy" || run_robot_tests == "false" || OSC_VM_IP == ''){
			echo 'Skipped'
		} else {
		    sh 'sed -ie \'s|${osc-ip}=.*|${osc-ip}=  '+OSC_VM_IP+'|g\' '+robotFrameworkTestsDir+'basic.robot'
		    try {
		        sh 'robot -d '+robotFrameworkTestsDir+'basic-results '+robotFrameworkTestsDir+'basic.robot'
		    } finally {
			    sh 'rm basic-results/ -rf'
			    sh 'mv '+robotFrameworkTestsDir+'basic-results $WORKSPACE/'
				stash name: "robot-output", includes: "basic-results/**/*"

				node('master') {
					// Run the unstash from within that directory!
					sh 'mkdir -p robot-output'
					dir("robot-output") {
						unstash "robot-output"

						/*step([$class: 'RobotPublisher',
                              disableArchiveOutput: false,
                              logFileName: 'log.html',
                              otherFiles: '',
                              outputFileName: 'output.xml',
                              outputPath: '.',
                              passThreshold: 100,
                              reportFileName: 'report.html',
                              unstableThreshold: 0]);*/

						if(buildVersion == "") {
						    sh 'sudo rm '+outputDir+'last_robot_tests -rf'
						    sh 'sudo mv -v basic-results/ ' + outputDir +"last_robot_tests"
						    echo "http://10.3.240.52/osc-ovf-parametrized/last_robot_tests/"
						} else {
						    sh 'sudo mv -v basic-results/ ' + outputDir + buildVersion + "/"
						    echo "http://10.3.240.52/osc-ovf-parametrized/"+buildVersion+"/robot-output/"
						}
					}
				}
			}
		}
	}

	stage('Reset test environment') {
		if(deploy == "nodeploy" || revert_test_environment == "false") {
			echo "Skipped test environment reset"
		} else {
			echo "Reverting test environment to zero configuration..."
			sh 'java -jar /home/jenkins/osc-tools/revert-snapshot/revert-snapshot.jar /home/klocwork/osc-tools/revert-snapshot/config.json'
			// echo "Waiting for openstack to get up..."
			// sh 'python /home/klocwork/osc-tools/check-openstack-health/check-openstack-health.py'
		}
	}   
}
